import  React,{Component} from 'react';
import {Card,Button,Table} from 'antd';
export default class User extends  Component {
  initColumns=()=>{
    this.columns=[
      {
        title: '用户名',
        dataIndex: 'username'
      },
      {
        title: '邮箱',
        dataIndex: 'email'
      },

      {
        title: '电话',
        dataIndex: 'phone'
      },
      {
        title: '注册时间',
        dataIndex: 'create_time',
      },
      {
        title: '所属角色',
        dataIndex: 'role_id',
      },
      {
        title: '操作',
      },
    ]
  };
  componentWillMount() {
    this.initColumns();
  }

  render() {
    const title=(
        <span>
          <Button type='primary'>创建用户</Button>
          <Button type='primary' disabled style={{marginLeft:20}}>更新权限</Button>
        </span>
    );

    return (
       <Card title={title}>
         <Table columns={this.columns}></Table>
       </Card>
    );
  }
}
