import  React,{Component} from 'react';
import {Card,Button,Table} from 'antd';

import {PAGE_SIZE} from '../../utils/contains';
import {reqRole} from '../../api/index';
export default class Role extends  Component {
  state={
    roles:[],
    role:{}
  };
  onRow=(row)=>{
    return {
      onClick:event=>{
       this.setState({
         role:row
       })
      }
    }
  };
  getRole=async ()=>{
    const result=await reqRole();
    if(result.status==0){
      this.setState({
        roles:result.data,
      })
    }
  };
  initColumns=()=>{
    this.columns=[
      {
        title: '角色名称',
        dataIndex: 'name'
      },
      {
        title: '创建时间',
        dataIndex: 'create_time',
      },
      {
        title: '授权时间',
        dataIndex: 'auth_time',
      },
      {
        title: '授权人',
        dataIndex: 'auth_name'
      },
    ]
  };
  componentWillMount() {
    this.initColumns();
  }
  componentDidMount() {
    this.getRole();
  }

  render() {
    const title=(
        <span>
          <Button type='primary'>创建角色</Button>
          <Button type='primary' disabled={this.state.role?'true':'false'} style={{marginLeft:20}}>更新权限</Button>
        </span>
    );
    const {role}=this.state;
    return (
        <Card title={title}>
          <Table
              onRow={this.onRow}
              columns={this.columns}
              dataSource={this.state.roles}
              bordered
              rowKey='_id'
              pagination={{defaultPageSize:PAGE_SIZE}}
              rowSelection={{type:'radio',selectedRowKeys:[role._id]}}
          />
        </Card>
    );
  }
}
