import  React,{Component} from 'react';
import {Card,Button,Table} from 'antd';
import {PAGE_SIZE} from '../../utils/contains'
export default class Role extends  Component {
  state={
    role:[],
  }
  initColumns=()=>{
    this.columns=[
      {
        title: '用户名',
        dataIndex: 'username'
      },
      {
        title: '邮箱',
        dataIndex: 'email'
      },

      {
        title: '电话',
        dataIndex: 'phone'
      },
      {
        title: '注册时间',
        dataIndex: 'create_time',
      },
      {
        title: '所属角色',
        dataIndex: 'role_id',
      },
      {
        title: '操作',
      },
    ]
  };
  componentWillMount() {
    this.initColumns();
  }

  render() {
    const title=(
        <span>
          <Button type='primary'>创建角色</Button>
          <Button type='primary' disabled style={{marginLeft:20}}>更新权限</Button>
        </span>
    );

    return (
        <Card title={title}>
          <Table
              columns={this.columns}
              bordered
              rowKey='_id'
              pagination={{defaultPageSize:PAGE_SIZE}}
          />
        </Card>
    );
  }
}
