import  React,{Component} from 'react';
import {Card,Button,Table} from 'antd';

import {PAGE_SIZE} from '../../utils/contains';
import {reqRole} from '../../api/index';
export default class Role extends  Component {
  state={
    role:[],
  };
  onRow=(row)=>{
    return {
      onClick:event=>{
        console.log(row);
      }
    }
  };
  initColumns=()=>{
    this.columns=[
      {
        title: '角色名称',
        dataIndex: 'name'
      },
      {
        title: '创建时间',
        dataIndex: 'create_time',
      },
      {
        title: '授权时间',
        dataIndex: 'auth_time',
      },
      {
        title: '授权人',
        dataIndex: 'auth_name'
      },
    ]
  };
  componentWillMount() {
    this.initColumns();
    this.setState({
      role:[
        {
          "menus": [
            "all",
            "/home",
            "/products",
            "/category",
            "/product",
            "/user",
            "/role",
            "/charts",
            "/charts/bar",
            "/charts/line",
            "/charts/pie",
            "/order"
          ],
          "_id": "5d84b9a8dc927d0826e229f8",
          "name": "经理",
          "create_time": 1568979368904,
          "__v": 0,
          "auth_name": "admin",
          "auth_time": 1568979404573
        },
        {
          "menus": [
            "/charts",
            "/charts/bar",
            "/charts/line",
            "/charts/pie",
            "/home"
          ],
          "_id": "5d84b9f9dc927d0826e229f9",
          "name": "科员",
          "create_time": 1568979449366,
          "__v": 0,
          "auth_name": "admin",
          "auth_time": 1568979464996
        }
      ]
    })
  }
  componentDidMount() {

  }

  render() {
    const title=(
        <span>
          <Button type='primary'>创建角色</Button>
          <Button type='primary' disabled style={{marginLeft:20}}>更新权限</Button>
        </span>
    );

    return (
        <Card title={title}>
          <Table
              onRow={this.onRow}
              columns={this.columns}
              dataSource={this.state.role}
              bordered
              rowKey='_id'
              pagination={{defaultPageSize:PAGE_SIZE}}
              rowSelection={{type:'radio'}}
          />
        </Card>
    );
  }
}
